import React, { Component } from "react";
import "./Hero.scss";
import Img from "../../assets/images/hero-image.png";

export default class Hero extends Component {
  render() {
    return (
      <section className="Hero">
        <img
          src={Img}
          srcSet="/assets/images/hero-image@2x.png 2x, /assets/images/hero-image@3x.png 3x"
          alt="logo"
          className="Hero__img"
        />
        <div className="row">
            <article className="hero__text">
                <div className="hero__text__head">
                    <span className="hero__text__head--title">With passion for</span>
                    <br/>
                    <span className="hero__text__head--subtitle">the future</span>
                </div>
            </article>
        </div>
      </section>
    );
  }
}
