import React, { Component } from "react";
import "./Duyurular.scss";
import Img from "../../assets/images/search.svg";
import Img2 from "../../assets/images/fs.svg";
import DuyurularCard from '../UI/Cards/DuyurularCard/DuyurularCard'

export default class Duyurular extends Component {

    state = { 
        text:{
            text1:"İzin Duyurusu",
            text2:"Yemekhane Duyurusu",
            text3:"Ulaşım Duyurusu",
            text4:"Sağlık Sigortası Duyurusu",
            text5:"Eğitim Duyuruları",
            text6:"İş Yeri Güvenliği Duyurusu",
        }
    };

  render() {

    return (
      <section className="Duyurular">
        <div className="Duyurular__content">
          <div className="Duyurular__content__title">
            <span>Duyurular</span>
          </div>
          <div className="Duyurular__content__buttons">
            <button className="Duyurular__content__buttons--search">
              <img src={Img} alt="search" />
            </button>
            <button className="Duyurular__content__buttons--fullscreen">
              <span>Genişlet</span>
              <img
                src={Img2}
                alt="search"
                srcSet="../../../assets/images/fs.svg@2x 2x,
                        ../../../assets/images/fs.svg@3x 3x"
              />
            </button>
          </div>
        </div>
        <div className="row">
          <DuyurularCard cardText={this.props.text.text1}/>
          <DuyurularCard cardText={this.props.text.text2}/>
          <DuyurularCard cardText={this.props.text.text3}/>
          <DuyurularCard cardText={this.props.text.text4}/>
          <DuyurularCard cardText={this.props.text.text5}/>
          <DuyurularCard cardText={this.props.text.text6}/>
        </div>
      </section>
    );
  }
}
