import React, { Component } from "react";
import "./Hero.scss";
import Img from "../../assets/images/hero-image.png";

export default class Hero extends Component {
  render() {
    return (
      <section className="Hero">
        <img
          src={Img}
          srcSet={`
                    ${require("../../assets/images/hero-image@2x.png")} x2, 
                    ${require("../../assets/images/hero-image@3x.png")} x3
        `}
          alt="logo"
          className="Hero__img"
        />
      </section>
    );
  }
}
